import axios from "axios";
import { useContext, useState } from "react";
import { RootContext } from "../context";
import styled from "styled-components";
import SearchResults from "./SearchResults";
import CategorySelector from "./CategorySelector";
import logo from "../assets/logo.png";
import options from "../assets/options.png";
import select from "../assets/select.png";
import read from "../assets/read.png";

const HomePage = () => {
    const [selectedCategories, updateSelectedCategories] = useState([] as any[]);
    const [mode, updateMode] = useState("Category Selection" as "Category Selection" | "Results");

    const { SERVER_URL } = useContext(RootContext);

    const testServer = async () => {
        console.log(SERVER_URL);
        console.log("Testing backend");
        console.log((await axios.get(SERVER_URL + "/test")).data);
    };

    return (
        <div style={{ textAlign: "center", width: "80%", margin: "auto" }}>
            <div
                style={{
                    display: "flex",
                    width: "max-content",
                    margin: "50px auto",
                    alignItems: "center",
                }}>
                <div style={{ margin: "20px" }}>
                    <img width="200px" src={logo} />
                </div>
                <div style={{ margin: "20px" }}>
                    <Title>New On YouTube</Title>
                    <SubTitle>Find your favourite videos!</SubTitle>
                </div>
            </div>

            <div style={{ display: "flex", justifyContent: "space-between" }}>
                <Panel>
                    <div
                        style={{
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                        }}>
                        <Number>1</Number>&nbsp;&nbsp;&nbsp;
                        <img src={options} width="75px" />
                    </div>
                    <PanelTitle>Choose your categories</PanelTitle>
                </Panel>
                <Panel>
                    <div
                        style={{
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                        }}>
                        <Number>2</Number>&nbsp;&nbsp;&nbsp;
                        <img src={select} width="75px" />
                    </div>
                    <PanelTitle>Select vidoes you like</PanelTitle>
                </Panel>
                <Panel>
                    <div
                        style={{
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                        }}>
                        <Number>3</Number>&nbsp;&nbsp;&nbsp;
                        <img src={read} width="75px" />
                    </div>
                    <PanelTitle>Checkout their details!</PanelTitle>
                </Panel>
            </div>

            <Start>Start New Search</Start>
        </div>
    );
};

export default HomePage;

const Title = styled.div`
    font-size: 50px;
    font-weight: bold;
    font-family: "Monaco", "Courier New", monospace;
`;

const SubTitle = styled.div`
    color: grey;
    font-size: 30px;
    font-weight: bold;
    font-family: "Monaco", "Courier New", monospace;
`;

const Panel = styled.div`
    width: 25%;
    padding: 20px;
    background-color: grey;
`;

const Number = styled.div`
    font-size: 50px;
    font-weight: bold;
    font-family: "Monaco", "Courier New", monospace;
`;

const PanelTitle = styled.div`
    font-size: 20px;
    font-weight: bold;
    font-family: "Monaco", "Courier New", monospace;
`;

const Start = styled.button`
    width: 40%;
    font-size: 40px;
    background-color: white;
    height: 40px;
    border: 2px solid #fad000;
    &:hover {
        background-color: #fad000;
        cursor: pointer;
    }
`;
