import { Dispatch, SetStateAction } from "react";
import styled from "styled-components";
import { Summary } from "../types";
import { thumbnail } from "../functions";

type SummaryModalProps = {
    updateSummaryModalOpen: Dispatch<SetStateAction<boolean>>;
    summary: Summary;
};

const SummaryModal = (props: SummaryModalProps) => {
    return (
        <Container onClick={() => props.updateSummaryModalOpen(false)}>
            <Modal>
                <div style={{ textAlign: "center" }}>
                    <Img src={thumbnail(props.summary.id)} />
                </div>
            </Modal>
        </Container>
    );
};

export default SummaryModal;

const Container = styled.div`
    position: absolute;
    width: 100vw;
    height: 100vw;
    background-color: rgba(0, 0, 0, 0.5);
`;

const Modal = styled.div`
    position: fixed;
    top: 0;
    width: 50%;
    height: 50%;
    margin: 250px 25vw;
    background-color: white;
    color: black;
`;

const Img = styled.img`
    margin: auto;
    border-radius: 10px;
    width: 40%;
`;

const Description = styled.div`
    text-align: center;
    width: 85%;
    font-size: 25px;
`;
