import { useState } from "react";
import styled from "styled-components";
import img from "./img.png";
import "./register.css";

const RegisterPage = () => {
    const [username, updateUsername] = useState("");
    const [password1, updatePassword1] = useState("");
    const [password2, updatePassword2] = useState("");

    const handleEmailChange = (e: any) => {
        updateUsername(e.target.value);
    };

    const handlePasswordChange = (e: any, idx: number) => {
        idx === 0 ? updatePassword1(e.target.value) : updatePassword2(e.target.value);
    };

    const validPassword = password1.length !== 0 && password1 === password2;

    return (
        <div className="signin_background">
            <div className="container">
                <form className="form">
                    <h2>REGISTER</h2>
                    <input
                        type="email"
                        name="email"
                        className="box"
                        placeholder="Enter Email"
                        onChange={handleEmailChange}
                    />
                    <input
                        type="password"
                        name="password"
                        className="box"
                        placeholder="Enter Password"
                        onChange={e => handlePasswordChange(e, 0)}
                    />
                    <input
                        type="password"
                        name="confirm password"
                        className="box"
                        placeholder="Confirm Password"
                        onChange={e => handlePasswordChange(e, 1)}
                    />
                    {!validPassword
                        ? password2.length > 0 && <div>Passwords do not match</div>
                        : password2.length > 0 && <div>Valid password!</div>}
                    <SubmitButton
                        active={validPassword && username.length > 0}
                        type="submit"
                        value="SIGN UP"
                        id="submit"
                    />
                </form>
                <div className="side">
                    <img src={img} alt="" />
                </div>
            </div>
        </div>
    );
};

export default RegisterPage;

const SubmitButton = styled.input<{ active: boolean }>`
    padding: 12px 30px;
    width: 40%;
    margin-top: 40px;
    background-color: black;
    opacity: ${props => (props.active ? "1" : "0.2")};
    color: white;
    font-weight: bold;
    border: none;
    outline: none;
    border-radius: 20px;
    &hover: {
        cursor: ${props => (props.active ? "pointer" : "none")};
        background-color: #750000;
    }
`;
