import { ReactEventHandler, useState } from "react";
import styled from "styled-components";
import NavBar from "../NavBar/Navbar";
import testCategories from "../test/test_categories.json";

const SearchPage = () => {
    const [searchValue, updateSearchValue] = useState("");
    const [selection, updateSelection] = useState([] as string[]);

    const handleSearchChange = (e: any) => {
        updateSearchValue(e.target.value);
    };
    return (
        <div>
            <NavBar />
            <Title>Select Your Categories!</Title>
            <TwoPanel>
                <LeftPanel>
                    <SearchBar placeholder="Search for topics" onChange={handleSearchChange} />
                    <CategoryContainer>
                        {testCategories.map(c => (
                            <Category
                                onClick={() => updateSelection(selection.concat([c]))}
                                selected={props.selectedCategories.includes(c)}>
                                {c}
                            </Category>
                        ))}
                    </CategoryContainer>
                </LeftPanel>
                <RightPanel>
                    <PanelTitle>Selected Topics</PanelTitle>
                    <CategoryContainer></CategoryContainer>
                </RightPanel>
            </TwoPanel>
            <SearchButton>Search!</SearchButton>
        </div>
    );
};

export default SearchPage;

const Title = styled.div`
    font-size: 60px;
    font-weight: bold;
    margin: 50px auto;
    width: max-content;
`;

const TwoPanel = styled.div`
    display: flex;
    justify-content: center;
    width: 80%;
    height: 50vh;
    margin: auto;
`;

const LeftPanel = styled.div`
    width: 50%;
    border-right: 2px solid black;
`;

const RightPanel = styled.div`
    width: 50%;
    border-left: 2px solid black;
`;

const SearchBar = styled.input``;

const PanelTitle = styled.div`
    font-size: 30px;
    font-weight: bold;
`;

const CategoryContainer = styled.div`
    display: flex;
    wrap: text-wrap;
`;

const Category = styled.button<{ selected: boolean }>`
    margin: 10px;
    font-size: 30px;
    padding: 10px;
    border-style: none;
    background-color: ${props => (props.selected ? "#FAD000" : "grey")};
    border-radius: 5px;
    &:hover {
        transform: scale(1.1);
        cursor: pointer;
    }
`;

const SearchButton = styled.div`
    width: 300px;
    margin: auto;
    font-size: 40px;
    text-align: center;
    background-color: black;
    color: white;
    border-style: none;
    border-radius: 5px;
    padding: 10px;
    &:hover {
        color: #fad000;
        cursor: pointer;
        transform: scale(1.1);
    }
`;
