import axios from "axios";
import { useContext, useState } from "react";
import { RootContext } from "../context";
import styled from "styled-components";
import SearchResults from "./SearchResults";
import CategorySelector from "./CategorySelector";
import logo from "../assets/logo.png";
import options from "../assets/options.png";

const HomePage = () => {
    const [selectedCategories, updateSelectedCategories] = useState([] as any[]);
    const [mode, updateMode] = useState("Category Selection" as "Category Selection" | "Results");

    const { SERVER_URL } = useContext(RootContext);

    const testServer = async () => {
        console.log(SERVER_URL);
        console.log("Testing backend");
        console.log((await axios.get(SERVER_URL + "/test")).data);
    };

    return (
        <div style={{ textAlign: "center", width: "80%", margin: "auto" }}>
            <div
                style={{
                    display: "flex",
                    width: "max-content",
                    margin: "50px auto",
                    alignItems: "center",
                }}>
                <div style={{ margin: "20px" }}>
                    <img width="200px" src={logo} />
                </div>
                <div style={{ margin: "20px" }}>
                    <Title>New On YouTube</Title>
                    <SubTitle>Find your favourite videos!</SubTitle>
                </div>
            </div>

            <div style={{ display: "flex", justifyContent: "space-between" }}>
                <Panel>
                    <Number>1</Number>
                    <div style={{ display: "flex", alignItems: "center" }}>
                        <img src={options} width="100px" />
                        <PanelTitle>Choose your categories</PanelTitle>
                    </div>
                </Panel>
                <Panel>
                    <Number>2</Number>
                    <PanelTitle>Select vidoes you like</PanelTitle>
                </Panel>
                <Panel>
                    <Number>3</Number>
                    <PanelTitle>Checkout their details!</PanelTitle>
                </Panel>
            </div>
            <ModeButtonContainer>
                <ModeButton
                    selected={mode == "Category Selection"}
                    onClick={() => updateMode("Category Selection")}>
                    Category Selection
                </ModeButton>
                <ModeButton selected={mode == "Results"} onClick={() => updateMode("Results")}>
                    Search!
                </ModeButton>
            </ModeButtonContainer>

            {mode == "Category Selection" && (
                <CategorySelector
                    selectedCategories={selectedCategories}
                    updateSelectedCategories={updateSelectedCategories}
                />
            )}
            {mode == "Results" && <SearchResults selectedCategories={selectedCategories} />}
        </div>
    );
};

export default HomePage;

const Title = styled.div`
    font-size: 50px;
    font-weight: bold;
    font-family: "Monaco", "Courier New", monospace;
`;

const SubTitle = styled.div`
    color: grey;
    font-size: 30px;
    font-weight: bold;
    font-family: "Monaco", "Courier New", monospace;
`;

const Panel = styled.div`
    width: 25%;
    padding: 20px;
    height: 150px;
    background-color: grey;
`;

const Number = styled.div`
    font-size: 50px;
    font-weight: bold;
    font-family: "Monaco", "Courier New", monospace;
`;

const PanelTitle = styled.div`
    font-size: 20px;
    font-weight: bold;
    font-family: "Monaco", "Courier New", monospace;
`;

const ModeButtonContainer = styled.div`
    display: flex;
    width: 100%;
    margin: auto;
    border-bottom: 2px solid #fad000;
    justify-content: center;
`;

const ModeButton = styled.button<{ selected: boolean }>`
    width: 40%;
    font-size: 25px;
    background-color: ${props => (props.selected ? "#fad000" : "white")};
    height: 40px;
    border: 2px solid #fad000;
    border-bottom: none;
    &:hover {
        background-color: ${props => (props.selected ? "#fad000" : "#fff1ac")};
        cursor: pointer;
    }
`;
