import React, { createContext } from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import { RootContext } from "./context";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import HomePage from "./HomePage/HomePage";
import SearchPage from "./SearchPage/SearchPage";
import FeedPage from "./FeedPage/FeedPage";

// conditionally switched to production url in live
const PRODUCTION: boolean = location.href.startsWith("http://localhost");
const SERVER_URL = PRODUCTION ? "https://new-on-youtube.herokuapp.com/" : "http://localhost:5000";

const router = createBrowserRouter([
    {
        path: "/",
        element: <HomePage />,
    },
    {
        path: "/Search",
        element: <SearchPage />,
    },
    {
        path: "/Feed",
        element: <FeedPage />,
    },
]);

ReactDOM.createRoot(document.getElementById("root") as HTMLElement).render(
    <React.StrictMode>
        <RootContext.Provider value={{ SERVER_URL }}>
            <RouterProvider router={router} />
        </RootContext.Provider>
    </React.StrictMode>
);
